import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { Link, Redirect } from "react-router-dom";
import UserAPI from "../API/UserAPI";
import { addSession } from "../Redux/Action/ActionSession";
import "./Auth.css";
import queryString from "query-string";
import CartAPI from "../API/CartAPI";

function SignIn(props) {
   //listCart Ä‘Æ°á»£c láº¥y tá»« redux
   const listCart = useSelector(
      (state) => state.Cart.listCart
   );

   const [email, setEmail] = useState("");

   const [password, setPassword] = useState("");

   const [user, setUser] = useState([]);

   const [errorEmail, setErrorEmail] = useState(false);
   const [emailRegex, setEmailRegex] = useState(false);
   const [errorPassword, setErrorPassword] = useState(false);

   const [redirect, setRedirect] = useState(false);

   const [checkPush, setCheckPush] = useState(false);

   const dispatch = useDispatch();

   const onChangeEmail = (e) => {
      setEmail(e.target.value);
   };

   const onChangePassword = (e) => {
      setPassword(e.target.value);
   };

   const onSubmit = () => {
      setErrorPassword(false);
      setEmailRegex(false);
      if (!email) {
         console.log("khong co ky tu trong truong email");
         setErrorEmail(true);
         return;
      }
      if (!password) {
         console.log("khong co ky tu trong truong pass");
         setErrorEmail(false);
         setErrorPassword(true);
         return;
      }
      if (!validateEmail(email)) {
         setEmailRegex(true);
         return;
      }
      const postLogin = async () => {
         try {
            const res = await UserAPI.postLogin(
               {
                  email,
                  password,
               },
               {
                  withCredentials: true,
               }
            );
            console.log("ðŸš€ ~ postLogin ~ res:", res);
            localStorage.setItem("user", JSON.stringify(res));
            const action = addSession(
               JSON.parse(localStorage.getItem("user"))._id
            );
            dispatch(action);
            setCheckPush(true);
         } catch (error) {
            console.log("ðŸš€ ~ postLogin ~ error:", error);
         }
      };
      postLogin();
   };

   //HÃ m nÃ y dÃ¹ng Ä‘á»ƒ Ä‘Æ°a háº¿t táº¥t cáº£ carts vÃ o API cá»§a user
   useEffect(() => {
      const fetchData = async () => {
         //Láº§n Ä‘áº§u sáº½ khÃ´ng thá»±c hiá»‡n insert Ä‘Æ°á»£c vÃ¬ addCart = ''
         if (checkPush === true) {
            for (let i = 0; i < listCart.length; i++) {
               //NÃ³ sáº½ láº¥y idUser vÃ  idProduct vÃ  count cáº§n thÃªm Ä‘á»ƒ gá»­i lÃªn server
               const params = {
                  idUser: localStorage.getItem("id_user"),
                  idProduct: listCart[i].idProduct,
                  count: listCart[i].count,
               };

               const query =
                  "?" + queryString.stringify(params);

               const response = await CartAPI.postAddToCart(
                  query
               );
               console.log(response);
            }

            setRedirect(true);
         }
      };

      fetchData();
   }, [checkPush]);

   function validateEmail(email) {
      const regex =
         /^(([^<>()[\]\\.,;:\s@"]+(\.[^<>()[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/;
      return regex.test(String(email).toLowerCase());
   }

   return (
      <div className='limiter'>
         <div className='container-login100'>
            <div className='wrap-login100 p-l-55 p-r-55 p-t-65 p-b-50'>
               <span className='login100-form-title p-b-33'>
                  Sign In
               </span>

               <div className='d-flex justify-content-center pb-5'>
                  {emailRegex && (
                     <span className='text-danger'>
                        * Incorrect Email Format
                     </span>
                  )}
                  {errorEmail && (
                     <span className='text-danger'>
                        * Please Check Your Email
                     </span>
                  )}
                  {errorPassword && (
                     <span className='text-danger'>
                        * Please Check Your Password
                     </span>
                  )}
               </div>

               <div className='wrap-input100 validate-input'>
                  <input
                     className='input100'
                     type='text'
                     placeholder='Email'
                     value={email}
                     onChange={onChangeEmail}
                  />
               </div>

               <div className='wrap-input100 rs1 validate-input'>
                  <input
                     className='input100'
                     type='password'
                     placeholder='Password'
                     value={password}
                     onChange={onChangePassword}
                  />
               </div>

               <div className='container-login100-form-btn m-t-20'>
                  {redirect && <Redirect to={`/`} />}
                  <button
                     className='login100-form-btn'
                     onClick={onSubmit}
                  >
                     Sign in
                  </button>
               </div>

               <div className='text-center p-t-45 p-b-4'>
                  <span className='txt1'>
                     Create an account?
                  </span>
                  &nbsp;
                  <Link to='/signup' className='txt2 hov1'>
                     Sign up
                  </Link>
               </div>
            </div>
         </div>
      </div>
   );
}

export default SignIn;
